//;(function(){
	/*
		所有关于生成游戏的内容区域
		var ROWS_NUM = 12,
			COLS_NUM = 8,
			PRIMARY_MINE_NUM = 15,
			SPECIALTY_MINE_NUM = 20,
			MASTER_MINE_NUM = 25,
			EXTREME_MINE_NUM = 30;
	*/
	var BG = 'data:image/jpeg;base64,',
		MINE = 'data:image/jpeg;base64,',
		FLAG = 'data:image/jpeg;base64,',
		LOST = 'data:image/png;base64,',
		MINEBANG = 'data:image/jpeg;base64,',
		NOTMINE = 'data:image/jpeg;base64,',
		TOPBG = 'data:image/jpeg;base64,',
		WIN = 'data:image/png;base64,'
		;
		
	function saveBest(state){
		window.localStorage.setItem('GameBest',JSON.stringify(state));
	}
	function restoreBest(){
		var best = window.localStorage.getItem('GameBest');
		if(best){
			return JSON.parse(best);
		}else{
			return null;
		}
	}
	var Game = {
		init:function(p){
			this.p = p;
			this.con = p.con;
			// this.clickState用来标记目前状态，是属于标记状态还是属于探测状态0-mark 1-probe;
			var _this 							= this;
			this.bar						 	= this.con.find('.actionBar');
			this.setTable();
			this.con.delegate('.actionBar' , 'tap', function(e){
				_this.changeClickS(_this,e);
			});
			this.con.delegate('.oneline', 'tap', function(e){
				_this.changeState(_this,e);
			});
			this.bar.delegate('.result' , 'tap', function(e){
				_this.close();
				_this.open(_this.p);
			});
			this.open(p);
		},
		setTable:function(){
			for( var i = 0; i < ROWS_NUM; i++ ){
				var tr = $('<tr class="oneline"></tr>')
				for(var j = 0; j < COLS_NUM; j++ ){
					var td = $('<td><div><input type="image" src='+TOPBG+' id="'+i+'_'+j+'"  /></div></td>');
					td.appendTo(tr);
				}
				tr.appendTo(this.con.find('.gamecon table').eq(0));
			}
		},
		open:function(p){
			this.clickState 					= 1;
			this.costTime 						= 0;								//记录游戏花费时间
			this.mineNum 						= p.mineNum||PRIMARY_MINE_NUM;
			this.currentMine                    = 0;
			this.startTime						= null;
			this.result                         = null;
			this.mineInfo.init(this);
			//this.bar.find('.probe').addClass('selected');
			this.bar.find('.probe').trigger('tap');
			this.bar.find('.marked').html(this.mineNum);
		},
		startCostTime: function(startT){
			var _this   = this;
			startT  = startT.getTime();
			this.payT = window.setInterval(function(){
				var	endT 			= new Date();
				endT 				= endT.getTime();
				_this.costTime   	= Math.floor((endT-startT)/1000); 
				_this.costTimeHandler(_this.costTime%999);
			},500);
		},
		endCostTime: function(){
			if(!this.payT) return;
			clearInterval(this.payT) ;
		},
		costTimeHandler: function(costTime){
			var h=t=n=0;
			var temp = costTime.toString();
			var length = temp.length; 
			if(length>=3)
				h = temp.charAt(length-3);
			if(length>=2)
				t = temp.charAt(length-2);
			if(length>=1)
				n = temp.charAt(length-1);
			this.bar.find('.timer').html(''+h+t+n);
		},
		mineInfo:{
			init: function(parent){
				this.parent = parent;
				this.Data = null;
				this.BuildData();
				this.GenerateMine();
			},
			BuildData: function(){
				/*M:代表雷区,S:代表状态0-nomal 1-flag 2-open 3-mine_yes 4-mine_no 5-mine*/
				this.Data = [];
				for( var x=0; x < ROWS_NUM; x++ ){
					this.Data[x] = [];
					for( var y=0; y < COLS_NUM; y++ ){
						this.Data[x][y] = {M:false,S:0,N:0};
					}
				}
			},
			GenerateMine: function(){
				var mine=0, Mine = this.parent.mineNum;
				while( mine < Mine ){
					var x = parseInt(Math.random() *10000) % ROWS_NUM;
					var y = parseInt(Math.random() *10000) % COLS_NUM;
					if(!this.Data[x][y].M){
						this.Data[x][y].M = true;
						mine++;
					}
				}
			}
		},
		changeClickS:function(ins,evt){
			var src = evt.target;
			if($(src).hasClass('mark') || $(src).parents('.mark').length > 0 ){
				ins.clickState = 0;
				$(src).hasClass('mark')?$(src).addClass('selected'):$(src).parents('.mark').addClass('selected');
				ins.bar.find('.probe').removeClass('selected');
			}else if($(src).hasClass('probe') || $(src).parents('.probe').length > 0 ){
				ins.clickState = 1;
				$(src).hasClass('probe')?$(src).addClass('selected'):$(src).parents('.probe').addClass('selected');
				ins.bar.find('.mark').removeClass('selected');
			}
		},
		changeState:function(ins,evt){
			var src = evt.target,
				tapID;
			if( src.nodeName != 'INPUT' || $(src).attr('type') != 'image' || this.result ) {return;}
			/*如何判断是第一次点击事件,如果是第一次点击则计时开始*/
			if( !this.startTime ){
				this.startTime = new Date();
				this.startCostTime(this.startTime);
			}
			tapID = $(src).attr('id').split('_');
			if( this.clickState === 1 ){
				this.serachMine(parseInt(tapID[0]),parseInt(tapID[1]));
			}else{
				this.markMine(parseInt(tapID[0]),parseInt(tapID[1]));
			}
		},
		cellIsValidated: function(x, y){
			return !( x < 0 || x >= ROWS_NUM || y < 0 || y >= COLS_NUM );
		},
		getMineNum: function(x,y){
			var result = 0;
			if( this.cellIsValidated(x-1, y-1) && this.mineInfo.Data[x-1][y-1].M )
				result++;
			if( this.cellIsValidated(x-1, y) && this.mineInfo.Data[x-1][y].M )
				result++;
			if( this.cellIsValidated(x-1, y+1) && this.mineInfo.Data[x-1][y+1].M )
				result++;
			if ( this.cellIsValidated(x, y-1) && this.mineInfo.Data[x][y-1].M )
				result++;
			if ( this.cellIsValidated(x, y+1) && this.mineInfo.Data[x][y+1].M )
				result++;
			if ( this.cellIsValidated(x+1, y-1) && this.mineInfo.Data[x+1][y-1].M )
				result++;
			if ( this.cellIsValidated(x+1, y) && this.mineInfo.Data[x+1][y].M )
				result++;
			if ( this.cellIsValidated(x+1, y + 1) && this.mineInfo.Data[x+1][y+1].M )
				result++;
			return result;
		},
		serachMine: function(x,y){
			if(!this.cellIsValidated(x,y)) return;
			/*如果不是自然状态并且也不是标记雷区状态*/
			if( this.mineInfo.Data[x][y].S != 0 && this.mineInfo.Data[x][y].S != 1 ) return;
			if(this.mineInfo.Data[x][y].S == 1){
				this.currentMine--;
				this.mineChange();
			}
			/*如果不是雷区*/
			if( !this.mineInfo.Data[x][y].M ){
				var mineNumber = this.getMineNum(x,y);
				if( mineNumber == 0){
					this.mineInfo.Data[x][y].S = 2;
					this.cellChange(x,y);
					this.serachMine(x-1, y-1);
					this.serachMine(x-1, y);
					this.serachMine(x-1, y + 1);
					this.serachMine(x, y - 1);
					this.serachMine(x, y + 1);
					this.serachMine(x + 1, y - 1);
					this.serachMine(x + 1, y);
					this.serachMine(x + 1, y + 1);
				 }else{
					this.mineInfo.Data[x][y].S = 2;
					this.mineInfo.Data[x][y].N = mineNumber;
					this.cellChange(x,y);
				 }
			 }else{ //如果是雷区则游戏结束
				this.mineInfo.Data[x][y].S = 5;
				this.cellChange(x,y);
				this.lose(x,y);
			 }
		},
		markMine: function(x,y){
			if(!this.cellIsValidated(x,y)) return;
			var isWin = false;
			if( this.mineInfo.Data[x][y].S == 0 ){
				this.mineInfo.Data[x][y].S = 1;
				this.currentMine++;
				this.cellChange(x,y);
				this.mineChange(x,y);
				isWin = this.judgeWin();
			}else if( this.mineInfo.Data[x][y].S == 1 ){
				this.mineInfo.Data[x][y].S = 0;
				this.currentMine--;
				this.cellChange(x,y);
				this.mineChange(x,y);
				isWin = this.judgeWin();
			}
		   this.cellChange(x,y);
		   if(isWin){
			   /*如果赢了*/
				this.win();
		   }
		},
		showMine: function(){
			for( var i = 0; i < ROWS_NUM; i++ ){
				for( var j = 0; j < COLS_NUM; j++ ){
					if(this.mineInfo.Data[i][j].S == 0 && this.mineInfo.Data[i][j].M){
						this.mineInfo.Data[i][j].S = 3;
						this.cellChange(i,j);
					}else if(this.mineInfo.Data[i][j].S == 1 && !this.mineInfo.Data[i][j].M){
						this.mineInfo.Data[i][j].S = 4;
						this.cellChange(i,j);
					}
				}
			}
		},
		record: function(){
			var best		= restoreBest();
			if(!best){best = {};}
			switch(this.mineNum){
				case window.PRIMARY_MINE_NUM :		if(!best.primary||best.primary > this.costTime)best.primary = this.costTime;break;
				case window.SPECIALTY_MINE_NUM :	if(!best.specialty||best.specialty > this.costTime)best.specialty = this.costTime;break;
				case window.MASTER_MINE_NUM :		if(!best.master||best.master > this.costTime)best.master = this.costTime;break;
				case window.EXTREME_MINE_NUM :		if(!best.extreme||best.extreme > this.costTime)best.extreme = this.costTime;break;
			}
			saveBest(best);
			console.log('recode',restoreBest());
		},
		win: function(){
			var _this = this;
			this.endCostTime();
			this.record();
			this.result = 'win';
			if(!this.winDialog){
				var dialog = this.winDialog = new $.EasyDialog({
					title: '继续',
					content: '赢得好爽，我要破纪录！',
					buttons: {
						'确定': function(){
							this.close();
							_this.close();
							_this.open(_this.p);
						},
						'取消': function(e,sender){
							this.close();
						 }
					}
				});
			}
			this.winDialog.open();
		},
		lose:function(){
			var _this = this;
			this.endCostTime();
			this.bar.find('.result img').attr('src',LOST);
			this.showMine();
			this.result = 'lose';
			if(!this.lostDialog){
				var dialog = this.lostDialog = new $.EasyDialog({
					title: '继续',
					content: '肿么就这样输掉了，我要翻盘！',
					buttons: {
						'确定': function(){
							this.close();
							_this.close();
							_this.open(_this.p);
						},
						'取消': function(e,sender){
							this.close();
						 }
					}
				});
			}
			this.lostDialog.open();
		},
		judgeWin:function(){
			/*
			 *	在里面判断是不是已经赢了
			 *	判断标准：	1.标记雷数和雷数相等；
			 *          	2.每个标记雷区是对应的雷区；
			 */
			if( this.currentMine != this.mineNum )return false;
			for( var i = 0; i < ROWS_NUM; i++ ){
				for( var j = 0; j < COLS_NUM; j++ ){
					if( this.mineInfo.Data[i][j].M && this.mineInfo.Data[i][j].S !== 1 ){
						return false;
					}
				}
			}
			return true;
		},
		getCellDom: function(x,y){
			return $('#'+x+'_'+y);
		},
		cellrestore:function(){
			for( var i = 0; i < ROWS_NUM; i++ ){
				for( var j = 0; j < COLS_NUM; j++ ){
					this.mineInfo.Data[i][j].S = 0;
					this.cellChange(i,j);
				}
			}
		},
		cellChange: function(x,y){
			var cell = this.getCellDom(x,y);
			if(!cell)return;
			if( this.mineInfo.Data[x][y].S == 0 ){
				cell.attr('src','image/topBg.jpg');
				cell.parent().find('.showNum').remove();
			}else if( this.mineInfo.Data[x][y].S == 1 ){
				cell.attr('src',FLAG);
			}else if( this.mineInfo.Data[x][y].S == 2 ){/*打开的状态*/
				if( this.mineInfo.Data[x][y].N > 0 ){
					cell.attr('src',BG);
					$('<input type="button" class="showNum color_'+this.mineInfo.Data[x][y].N+'" value="'+this.mineInfo.Data[x][y].N+'" />').insertAfter(cell);
				}else{
					cell.attr('src',BG);
				}
			}else if( this.mineInfo.Data[x][y].S == 3 ){
				cell.attr('src',MINE);
			}else if( this.mineInfo.Data[x][y].S == 4 ){
				cell.attr('src',NOTMINE);
			}else if( this.mineInfo.Data[x][y].S == 5 ){
				cell.attr('src',MINEBANG);
			}
		},
		mineChange: function(x,y){
			//关于做标记后的情况
			if(this.currentMine>this.mineNum)return;
			var t=n=0;
			var temp = (((this.mineNum-this.currentMine)>0)?(this.mineNum-this.currentMine):0).toString();
			var length = temp.length; 
			if(length >= 2)
				t = temp.charAt(length-2);
			if(length >= 1)
				n = temp.charAt(length-1);
			this.bar.find('.marked').text('' + t + n);
    	},
		resetGameP:function(){
			this.bar.find('.result img').attr('src',WIN)
			this.bar.find('.timer').html('000');
			this.bar.find('.marked').html(this.mineNum);
		},
		stopGame:function(){
			
		},
		close:function(){
			this.con.find('.oneline').unbind();
			this.resetGameP();
			this.endCostTime();
			this.cellrestore();
		},
		exit:function(){
			this.con.find('.oneline').remove();
			this.con.undelegate();
			this.bar.undelegate();
			this.close();
		},
	}
//})();